class NonBracket(ValueError):
    pass

def move_up_down(movements):
    ground = 0
    i = 0
    for move in list(movements):
        i += 1
        if move == '(':
            ground += 1
        elif move == ')':
            ground -= 1
        else:
            raise NonBracket(move)
        #if ground == -1:
        #    print("Reached basement at char {}".format(i))
    return ground

